mapper.insert:
insert into truck (id, license_plate, user_id, 
  max_capacity, recent_usage_time, usage_count
  )
values (#{id,jdbcType=INTEGER}, #{licensePlate,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, 
  #{maxCapacity,jdbcType=DOUBLE},now(), 0
  )

mapper.select:
select count(*)
from truck
where user_id = #{userId}
and license_plate = #{licensePlate}

select id, license_plate as licensePlate, max_capacity as maxCapacity from truck where user_id = #{userId}
and license_plate like concat('%', #{licensePlate}, '%')
order by recent_usage_time desc, usage_count desc
